svg.script:
function createCube(x,y){

    const cubes = document.getElementById("cubes");

    // var newElement = document.createElementNS("http://www.w3.org/2000/svg", 'use'); //Create a path in SVG's namespace
    // newElement.setAttribute("xlink:href","#cube0"); //Set path's data
    // newElement.setAttribute("x",x); //Set path's data
    // newElement.setAttribute("y",y); //Set path's data
    // cubes.appendChild(newElement);

    var newElement3 = document.createElementNS("http://www.w3.org/2000/svg", 'rect');
    newElement3.setAttribute("fill","blue");
    newElement3.setAttribute("x",String(x));
    newElement3.setAttribute("y",String(y));
    newElement3.setAttribute("width","50");
    newElement3.setAttribute("height","50");
    cubes.appendChild(newElement3);

}

createCube(0,0);
createCube(100,0);
createCube(200,0);
createCube(200,100);
createCube(200,200);
